<!-- Mega Menu JavaScript -->
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const dropdown = document.querySelector('.dropdown');
            const megaMenu = document.querySelector('.mega-menu');
            const practiceAreasDropdown = document.querySelector('#practiceAreasDropdown').closest('.dropdown');
            const practiceAreasMegaMenu = document.querySelector('.practice-areas-mega-menu');
            
            // Handle Find a Lawyer dropdown
            if (dropdown && megaMenu) {
                let hoverTimeout;
                
                // Function to calculate and set the correct top position
                function updateMenuPosition() {
                    const navbar = document.querySelector('.navbar');
                    if (navbar) {
                        const navbarRect = navbar.getBoundingClientRect();
                        const navbarBottom = navbarRect.bottom;
                        megaMenu.style.top = navbarBottom + 'px';
                    }
                }
                
                // Function to show menu
                function showMenu() {
                    clearTimeout(hoverTimeout);
                    updateMenuPosition();
                    megaMenu.classList.add('show');
                }
                
                // Function to hide menu
                function hideMenu() {
                    hoverTimeout = setTimeout(() => {
                        megaMenu.classList.remove('show');
                    }, 100); // Small delay to prevent flickering
                }
                
                // Update position on window resize
                window.addEventListener('resize', updateMenuPosition);
                
                // Add hover event listeners to dropdown
                dropdown.addEventListener('mouseenter', showMenu);
                dropdown.addEventListener('mouseleave', hideMenu);
                
                // Add hover event listeners to mega menu
                megaMenu.addEventListener('mouseenter', showMenu);
                megaMenu.addEventListener('mouseleave', hideMenu);
                
                // Make "Find a Lawyer" text clickable to navigate to find-a-lawyer.html
                const findLawyerLink = document.querySelector('#findLawyerDropdown');
                if (findLawyerLink) {
                    findLawyerLink.addEventListener('click', function(e) {
                        // Check if the click was on the text (not the dropdown arrow)
                        const clickTarget = e.target;
                        const isTextClick = clickTarget === findLawyerLink || clickTarget.textContent === 'Find a Lawyer';
                        
                        if (isTextClick) {
                            // Navigate to find-a-lawyer.html
                            window.location.href = 'find-a-lawyer.html';
                        }
                    });
                }
                
                // Old dynamic content code removed - now using static content per city
            }
            
            // Handle Practice Areas dropdown
            if (practiceAreasDropdown && practiceAreasMegaMenu) {
                let practiceAreasHoverTimeout;
                
                // Function to calculate and set the correct top position for Practice Areas
                function updatePracticeAreasMenuPosition() {
                    const navbar = document.querySelector('.navbar');
                    if (navbar) {
                        const navbarRect = navbar.getBoundingClientRect();
                        const navbarBottom = navbarRect.bottom;
                        practiceAreasMegaMenu.style.top = navbarBottom + 'px';
                    }
                }
                
                // Function to show Practice Areas menu
                function showPracticeAreasMenu() {
                    clearTimeout(practiceAreasHoverTimeout);
                    updatePracticeAreasMenuPosition();
                    practiceAreasMegaMenu.classList.add('show');
                }
                
                // Function to hide Practice Areas menu
                function hidePracticeAreasMenu() {
                    practiceAreasHoverTimeout = setTimeout(() => {
                        practiceAreasMegaMenu.classList.remove('show');
                    }, 200); // Increased timeout to allow clicks
                }
                
                // Prevent menu from closing when clicking on links
                practiceAreasMegaMenu.addEventListener('mousedown', function(e) {
                    if (e.target.closest('a')) {
                        clearTimeout(practiceAreasHoverTimeout);
                    }
                });
                
                // Update position on window resize
                window.addEventListener('resize', updatePracticeAreasMenuPosition);
                
                // Add hover event listeners to Practice Areas dropdown
                practiceAreasDropdown.addEventListener('mouseenter', showPracticeAreasMenu);
                practiceAreasDropdown.addEventListener('mouseleave', hidePracticeAreasMenu);
                
                // Add hover event listeners to Practice Areas mega menu
                practiceAreasMegaMenu.addEventListener('mouseenter', showPracticeAreasMenu);
                practiceAreasMegaMenu.addEventListener('mouseleave', hidePracticeAreasMenu);
                
                // Ensure links in practice area headers work properly
                practiceAreasMegaMenu.addEventListener('click', function(e) {
                    // Allow clicks on links to navigate normally
                    if (e.target.closest('.practice-title a')) {
                        // Link click - allow default navigation
                        return true;
                    }
                });
            }
        });
        
        // Function to change location and update button text for the bottom-right button only
        function changeLocation(location, element) {
            // Remove active class from all location links
            document.querySelectorAll('.mega-menu-link').forEach(link => {
                link.classList.remove('active');
            });
            
            // Add active class to clicked link
            element.classList.add('active');
            
            // Update button text for the bottom-right button only
            const buttonText = `View All Lawyers in ${location}`;
            const bottomButtonTextSpan = document.getElementById('viewAllLawyersTextBottom');
            
            if (bottomButtonTextSpan) {
                bottomButtonTextSpan.textContent = buttonText;
            }
            
            // Show different content based on city
            if (location === 'Dubai') {
                restoreDubaiContent();
            } else if (location === 'Abu Dhabi') {
                restoreAbuDhabiContent();
            } else {
                // Show blank content for all other cities
                showBlankContent();
            }
        }
        
